#include<iostream>
#include<vector>
#include"ListNode.h"
using namespace std;
ListNode<int>* make(void)
{
   ListNode<int>*dummyhead=new ListNode<int>(-1);
   ListNode<int>*cur=dummyhead;
   int num;
   cout<<"请输入链表结点个数";
   cin>>num;
   for(int i=0;i<num;i++)
   {
      int val;
      cin>>val;
      cur->next=new ListNode<int>(val);
      cur=cur->next;
   }
  ListNode<int>*head=dummyhead->next;
  delete dummyhead;
  return head;
}
void outpute(ListNode<int>*head)
{
    while(head!=nullptr)
    {
        cout<<head->val<<' ';
        head=head->next;
    }
}
int NumsofList(ListNode<int>*head)
{
    
    int res=0;
    while(head!=nullptr){
        head=head->next;
        res++;
    }
    return res;
}
ListNode<int>*cut(ListNode<int>*head,int n)
{
    ListNode<int>*cur;
    
    for(int i=1;i<n;i++)
    {
         head=head->next;
    }
    cur=head;
    head->next=nullptr;
    return cur;
}
ListNode<int>*merge(ListNode<int>*l1,ListNode<int>*l2)
{
    ListNode<int>*cur=new ListNode<int>(-1);
    ListNode<int>*head=cur;
    while(l1!=nullptr&&l2!=nullptr)
    {
        if(l1->val<l2->val){
            cur->next=l1;
            l1=l1->next;
        }else{
            cur->next=l2;
            l2=l2->next;
        }
        cur=cur->next;
    }
    cur->next=l1==nullptr?l2:l1;
    auto temp=cur;
    while(temp!=nullptr){
        temp=temp->next;
    }
    return head->next;
}
vector<ListNode<int>*>Lis_of_num(void)
{
    cout<<"请输入数组链表元素的大小";
    int num;
    cin>>num;
    vector<ListNode<int>*>nums(num);
    for(int i=0;i<num;i++)
    {
        nums[i]=make();
    }
    return  nums;
}
ListNode<int>*mergenums(vector<ListNode<int>*>&nums,int l,int r)
{
    if(l==r){
        return nums[l];
    }
    int mid=(l+r)/2;
    return merge(mergenums(nums,l,mid),mergenums(nums,mid+1,r));

}
int main(void)
{
    vector<ListNode<int>*>nums=Lis_of_num();
    ListNode<int>*head=mergenums(nums,0,nums.size()-1);
    outpute(head);
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       




